# Two pointer solution

def is_sequence(s,t):
    i = 0
    j = 0
    
    while i < len(s) and j < len(t):
        if s[i] == t[j]:
            i += 1
            j += 1
        else:
            j += 1
    if i == len(s):
        print("True")
    else:
        print('False')     
    
#is_sequence("acb", "ahbgdc")
#is_sequence("abc", "abbdxc")       
is_sequence("leeeeetcode",'""')